sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   var ga = new GlideAjax('GetEmailAddress');
   ga.addParam('sysparm_name',"getEmail");
   ga.addParam('sysparm_userid',g_form.getValue('u_requested_for'));
   ga.getXML(retriveEmailAddress);

   function retriveEmailAddress(response)
	{
		var emailID =  response.responseXML.documentElement.getAttribute("answer");
		g_form.addInfoMessage("Client Ajax Call Value of Email "+emailID);
		g_form.clearValue('u_email_address');
		g_form.setValue('u_email_address',emailID);
	}



   //Type appropriate comment here, and begin script below

}
