sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDateValidate : function(strdate)
	{
		var rttime = new GlideDateTime();
		var whneeded = new GlideDateTime(strdate);
		if(whneeded.before(rttime))
			{
				return true;
			}
		else
			{
				return false;
			}
	},
	isToday : function(strdate)
	{
		var rttime = new GlideDateTime();
		var whneeded = new GlideDateTime(strdate);
		var rtdate = rttime.getLocalDate();
		var whneedDate = whneeded.getLocalDate();
		if(rtdate.compareTo(whneedDate)==0)
			{
			return true;
			}
		else
			{
				return false;
			}
	},

    type: 'NeedItUtils'
};
